import React from "react";
import { Button } from "react-bootstrap";
import Header from "../../Components/Header/Header";
import "./Hero.css";
import data from "./data.json";
import Lottie from "lottie-react";

export default function Hero() {
  const lottieRef = React.useRef();
  const interactivity = {
    mode: "scroll",
    actions: [
      {
        visibility: [0, 0.2],
        type: "stop",
        frames: [0],
      },
      {
        visibility: [0.2, 0.45],
        type: "loop",
        frames: [0, 400],
      },
      {
        visibility: [0.45, 1.0],
        type: "loop",
        frames: [400, 600],
      },
    ],
  };
  return (
    <div className="hero">
      <Header />
      <img src="./assets/coin.png" className="coin" />
      <Lottie
        lottieRef={lottieRef}
        animationData={data}
        interactivity={interactivity}
      />
    </div>
  );
}
