  }, []);

  const Play = () => {
    const c = lottieRef.current.play();
  };
  const Pause = () => {
    const c = lottieRef.current.pause();
  };

  return (
    <div className="hero">
      <Header />
      <img src="./assets/coin.png" className="coin" />
      <Lottie
        lottieRef={lottieRef}
        animationData={data}
        loop={true}
