import React from "react";
import { Button } from "react-bootstrap";
import { useRef } from "react";
import Header from "../../Components/Header/Header";
import "./Hero.css";
import machine from "./machine.json";
import data from "./data.json";
import Lottie from "lottie-react";

export default function Hero() {
  const [ro, setRo] = React.useState(0);
  const sty = {
    position: "absolute",
    right: "50%",
    transform: `rotateY(${ro}deg)`,
    bottom: "0px",
    width: "100px",
    transition: "transform all 0.4s",
  };
  const lottieRef = useRef(null);
  React.useEffect(() => {
    lottieRef.current.pause();
    lottieRef.current.play();
  }, []);

  window.addEventListener("scroll", () => {
    setRo(window.scrollY);
  });

  const Play = () => {
    const c = lottieRef.current.play();
  };
  const Pause = () => {
    const c = lottieRef.current.pause();
  };

  return (
    <div className="hero">
      <Header />
      <p>Elrond's First Platform for Lending and Borrowing Crypto Assets</p>
      <Lottie
        lottieRef={lottieRef}
        animationData={data}
        loop={true}
        style={{
          height: "300px",
          position: "absolute",
          left: "0px",
          bottom: "0px",
        }}
      />
      <Lottie
        lottieRef={lottieRef}
        animationData={machine}
        loop={true}
        style={{
          height: "300px",
          position: "absolute",
          right: "50px",
          bottom: "0px",
        }}
      />
      <img src="./assets/coin.png" style={sty} />
    </div>
  );
}
